// Two types of selectors
// 1. input selector - doesn't use createSelector
// 2. output selector - does use input selectors and createSelector
import { createSelector } from 'reselect';

// This is an object that maps the string value to the respective id, where the string value that
// we're getting from our url parameter, will be the actual prop
// We're using this object, because our url parameter is a string whereas the id we want to match is
// a number
const COLLECTION_ID_MAP = {
	// '/hats' gets id=1 and so on
	// Actually the url string value goes to the id 
	hats: 1,
	sneakers: 2,
	jackets: 3,
	womens: 4,
	mens: 5
};

// Input selector that is a function that gets the state and just returns the state.shop
const selectShop = state => state.shop;

// Output selector
export const selectCollections = createSelector(
	// Takes a collection (array) of input selectors as first argument
	// Takes a function that returns the value that we want out of the selector as second argument
	[selectShop],
	shop => shop.collections
);

// Output selector. We pass in the collectionUrlParam, which is a string, and we'll return
// cretaeSelector, which in this case is a function that returns another function
export const selectCollection = collectionUrlParam => createSelector(
	// Returns createSelector call that gets selectCollections
	[selectCollections],
	// collection goes to collection.id is equal to COLLECTION_ID_MAP using collectionUrlParam
	// We actually call .find on the collections array, where we're matching the correct id to our
	// COLLECTION_ID_MAP using our string parameter value that we get from the url
	collections => 
		collections.find(
			collection => collection.id === COLLECTION_ID_MAP[collectionUrlParam]
		)
);
